mapper.select:
select tpb.PERSON_ID, tpb.PERSON_NAME, tpb.PROFILE_PHOTO, tpb.PERSON_NO, tpb.SEX, CASE tpb.SEX when 0 then '保密' when 1 then '男' when 2 then '女' else '' END as SEX_NAME, tpb.IDTYPE, tpb.IDCARD, tpb.PHONE, tpb.TEL, tpb.OFFICE, tdp.ASCN_ID, tpb.TENANT_ID, tdp.DEPT_ID as deptId, tdp.DEPT_SIMPLE_NAME as deptSimpleName, tdp.DEPT_FULL_NAME as deptFullName, tpd.DEFAULT_DEPT, CASE tpd.DEFAULT_DEPT when 1 then '主部门' else '兼职部门' END as defaultDeptName from tp_person_basicinfo tpb left join tp_person_dept tpd on tpb.PERSON_ID = tpd.PERSON_ID left join tp_dept_basicinfo tdp on tpd.dept_id = tdp.dept_id and tdp.ACTIVED = 1
left join tp_account ta on tpb.PERSON_ID = ta.PERSON_ID and ta.ACTIVED = 1
where tpb.ACTIVED = 1 and tpb.PERSON_ID != '1111111111111111111'
AND tpd.DEPT_ID = #{query.deptId}
AND tdp.DEPT_LEVELCODE like CONCAT(#{query.deptLevelcode}, '%')
AND tpb.PERSON_NAME like CONCAT('%', #{query.personName}, '%')
and tpb.SEX = #{query.sex}
and tpb.PHONE like CONCAT(#{query.phone}, '%')
and tpb.CATEGORY = #{query.category}
and ta.USERNAME = #{query.username}

select tpb.PERSON_ID, tpb.PERSON_NAME, tpb.PHONE from tp_person_basicinfo tpb
WHERE tpb.ACTIVED = 1
and tpb.PHONE = #{phone}
and tpb.PERSON_ID != #{personId}
limit 1

mapper.update:
update tp_person_basicinfo tpb set tpb.ASCN_ID = #{ascnId} where tpb.ASCN_ID = #{oldAscnId} and
tpb.PERSON_ID in (select tpd1.PERSON_ID from tp_person_dept tpd1 where tpd1.DEPT_ID in

    #{item}

and DEFAULT_DEPT = 1
)

UPDATE tp_person_basicinfo
   SET  ACTIVED = 0
      , UPDATOR = #{updator}
      , UPDATE_TIME = #{updateTime}
      , PHONE = concat(PHONE, '#', #{updateTime})
WHERE ASCN_ID = #{ascnId}
