mapper.select:
SELECT t.TAG_ID, t.TAG_NAME, t.TAG_DESC, t.TAG_COLOR, t.ORDER_INDEX, t.TENANT_ID, t.ASCN_ID, t.ACTIVED, t.LOG_DELETE, t.CREATOR, t.CREATE_TIME, t.UPDATOR, t.UPDATE_TIME, t.EXTEND01, t.EXTEND02, t.EXTEND03, p.PERSON_NAME as updatePersonName FROM tp_tag t LEFT JOIN tp_person_basicinfo p ON t.UPDATOR = p.PERSON_ID
1=1
AND t.TAG_NAME LIKE CONCAT('%', #{query.tagName}, '%')
AND t.TAG_DESC LIKE CONCAT('%', #{query.tagDesc}, '%')
AND t.TAG_COLOR = #{query.tagColor}
AND t.TENANT_ID = #{query.tenantId}
AND t.ASCN_ID = #{query.ascnId}
AND t.ACTIVED = #{query.actived}
AND t.LOG_DELETE = #{query.logDelete}
ORDER BY t.ORDER_INDEX ASC, t.CREATE_TIME DESC

SELECT t.TAG_ID, t.TAG_NAME, t.TAG_DESC, t.TAG_COLOR, t.ORDER_INDEX, t.TENANT_ID, t.ASCN_ID, t.ACTIVED, t.LOG_DELETE, t.CREATOR, t.CREATE_TIME, t.UPDATOR, t.UPDATE_TIME, t.EXTEND01, t.EXTEND02, t.EXTEND03, p.PERSON_NAME as updatePersonName FROM tp_tag t LEFT JOIN tp_person_basicinfo p ON t.UPDATOR = p.PERSON_ID
1=1
AND t.TAG_NAME LIKE CONCAT('%', #{query.tagName}, '%')
AND t.TAG_DESC LIKE CONCAT('%', #{query.tagDesc}, '%')
AND t.TAG_COLOR = #{query.tagColor}
AND t.TENANT_ID = #{query.tenantId}
AND t.ASCN_ID = #{query.ascnId}
AND t.ACTIVED = #{query.actived}
AND LOG_DELETE = #{query.logDelete}
ORDER BY ORDER_INDEX ASC, CREATE_TIME DESC

mapper.update:
UPDATE tp_tag
TAG_NAME = #{tagName}, TAG_DESC = #{tagDesc}, TAG_COLOR = #{tagColor}, ORDER_INDEX = #{orderIndex}, TENANT_ID = #{tenantId}, ASCN_ID = #{ascnId}, ACTIVED = #{actived}, LOG_DELETE = #{logDelete}, UPDATOR = #{updator}, UPDATE_TIME = #{updateTime}, EXTEND01 = #{extend01}, EXTEND02 = #{extend02}, EXTEND03 = #{extend03}
WHERE TAG_ID = #{tagId}

UPDATE tp_tag
SET LOG_DELETE = 1,
    UPDATOR = #{updator},
    UPDATE_TIME = #{updateTime}
WHERE TAG_ID = #{tagId}

