mapper.update:
UPDATE tp_message_read
READ_TIME = #{readTime}, MSG_TITLE = #{msgTitle}, MSG_TYPE = #{msgType}, MSG_SCOPE = #{msgScope}, MSG_GROUP = #{msgGroup}, MSG_FROM = #{msgFrom}, MSG_FROM_NAME = #{msgFromName}, MSG_TO = #{msgTo}, MSG_TO_NAME = #{msgToName}, LEVELCODE = #{levelcode}, MSG_CONTEXT = #{msgContext}, ACTIVED = #{actived}, EXTEND01 = #{extend01}, EXTEND02 = #{extend02}, EXTEND03 = #{extend03}, CREATE_TIME = #{createTime},
WHERE PERSON_ID = #{personId}

mapper.select:
SELECT PERSON_ID, READ_TIME, MSG_ID, MSG_TITLE, MSG_TYPE, MSG_SCOPE, MSG_GROUP, MSG_FROM, MSG_FROM_NAME, MSG_TO, MSG_TO_NAME, LEVELCODE, MSG_CONTEXT, ACTIVED, EXTEND01, EXTEND02, EXTEND03, CREATE_TIME FROM tp_message_read tmr WHERE ACTIVED = 1 AND tmr.PERSON_ID = #{query.personId}
AND tmr.MSG_ID = #{query.msgId}
AND tmr.MSG_TITLE like CONCAT(#{query.msgTitle}, '%')
ORDER BY CREATE_TIME desc
