mapper.update:
UPDATE tp_richtext

    CONTENT = #{content},

WHERE REFER_ID = #{referId}
and TXT_TYPE = #{txtType}

mapper.select:
SELECT TXT_ID, TXT_TYPE, REFER_ID, CONTENT FROM tp_richtext
WHERE
TXT_ID = #{txtId}

SELECT TXT_ID, TXT_TYPE, REFER_ID, CONTENT
FROM tp_richtext
WHERE REFER_ID = #{referId}
  and TXT_TYPE = #{txtType}
limit 1
