mapper.select:
select tpr.PERSON_ID as personId, tpr.ROLE_ID as roleId, tr.ROLE_NAME as roleName from tp_person_role tpr
left join tp_role tr on tpr.ROLE_ID = tr.ROLE_ID
where tpr.DEPT_ID = #{deptId}
and
tpr.PERSON_ID = #{personId}

mapper.delete:
DELETE FROM tp_person_role
where DEPT_ID = #{deptId}
  and PERSON_ID = #{personId}

DELETE FROM tp_person_role where PERSON_ID = #{personId} and
DEPT_ID in

    #{deptId}
